import React, { useState, useEffect } from 'react';
import { Text, Image, View, ImageBackground, Button, TextInput, ToastAndroid } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import PropTypes from 'prop-types';
import api from '../../services/api';
import { StatusBar } from 'react-native';
import 'react-native-gesture-handler';
import { StyleSheet, ScrollView } from 'react-native';
import Orientation from 'react-native-orientation-locker';



const avisar = (message) => {
    ToastAndroid.showWithGravityAndOffset(
        message,
        ToastAndroid.LONG,
        ToastAndroid.BOTTOM,
        25,
        50
    );

    return null;
};



export default function Main({navigation}) {
    
    const [dados, setDados] = useState(0);

    
  

    useEffect(() => {

        async function dadosIniciais() {
            try {

                //avisar(" " + token )

                const headers = {
                    'Authorization': 'Bearer ' + await AsyncStorage.getItem('@eContractRh:token')
                }

                console.log(headers)

                await api.get("/v1/protected/funcionarios/" + await AsyncStorage.getItem('@eContractRh:cpf'), {
                    headers: headers
                }).then(function (response) {
                    setDados(response.data);
                    const id_func = ''+ response.data.id_funcionario;
                    console.log("id salvo: " + id_func);
                    AsyncStorage.setItem('@eContractRh:id_func', id_func)
                });

              

            } catch (_err) {
                // avisar('Houve um problema com o login, verifique suas credenciais! ' + cpf + " " + senha );
                avisar(" " + _err);
                console.log(" " + _err);
               
            }

        }

    
        dadosIniciais();
    }, []);


    return (

        <View style={styles.container}>
            <StatusBar hidden />

            <ScrollView style={{width:'100%',height:'100%'}}>
                <View style={{width:'100%',height:'100%'}}>
            <View style={styles.coluna}>
                <LinhaMeio campo="Dados Iniciais" />
                <Linha campo="Nome:" valor={dados.nome + " " + dados.sobrenome} />
                <Linha campo="Gênero:" valor={dados.genero} />
                <Linha campo="CPF:" valor={dados.cpf} />
                <Linha campo="RG:" valor={dados.rg} />
                <Linha campo="Nacionalidade:" valor={dados.nacionalidade} />
                <Linha campo="Naturalidade:" valor={dados.naturalidade} />
                <Linha campo="Nascimento:" valor={dados.nascimento} />
                <Linha campo="Estado Civil:" valor={dados.estado_civil} />
                <Linha campo="Nome Mãe:" valor={dados.nome_mae} />
                <Linha campo="Nome Pai:" valor={dados.nome_pai} />
                <LinhaMeio campo="Endereço" />
                <Linha campo="Logradouro:" valor={dados.rua} />
                <Linha campo="Número:" valor={dados.numero} />
                <Linha campo="Bairro:" valor={dados.bairro} />
                <Linha campo="Cidade:" valor={dados.cidade} />
                <Linha campo="Estado:" valor={dados.estado_endereco} />
                <Linha campo="CEP:" valor={dados.cep} />

            </View>
            </View>
            </ScrollView>
        </View>
    );


}

const Linha = (props) => {
    return (
        <View style={styles.linha}>

            <View >
                <Text style={{ color: 'black', margin: 3, width: 150, textAlign: 'right' }} > {props.campo} </Text>
            </View>
            <View >
                <Text style={{ borderBottomWidth: 1, borderColor: '#fff', color: 'black', textTransform: 'capitalize' }} > {props.valor} </Text>
            </View>

        </View>
    );
}

const LinhaMeio = (props) => {
    return (
        <View style={{ backgroundColor: 'rgba(64,224,208,1)' }}>

            <Text style={{ fontSize: 16, color: 'white', margin: 2, padding: 5, textAlign: 'center' }} > {props.campo} </Text>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },
    backgroundImage: {
        resizeMode: 'cover',
        position: 'absolute',
        width: '100%',
        height: '100%',
    },
    linha: {
        flexDirection: "row",
        borderColor: '#FFF',
        backgroundColor: 'rgba(0,0,0,0.0)', // 40% opaque
        color: 'black',
        fontSize: 16,
        fontWeight: 'bold'

    },

    coluna: {
        flexDirection: "column",
        borderColor: '#FFF',
        backgroundColor: 'rgba(152,251,152,0.2)', // 40% opaque
        color: 'black',
        fontSize: 16,
        fontWeight: 'bold'

    },
});
