import { FlatList, Button, ScrollView, Image, TextInput, View, SafeAreaView, StatusBar, StyleSheet, ImageBackground, Text, ToastAndroid, TouchableOpacity, DatePickerIOSComponent } from "react-native";
import React, { useState, useEffect } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import PropTypes from 'prop-types';
import api from '../../services/api';
import 'react-native-gesture-handler';
import Orientation from 'react-native-orientation-locker';
import moment from 'moment';
import DateTimePicker from '@react-native-community/datetimepicker';
import {
	styles
} from '../../styles/styles';
import * as ScreenOrientation from 'expo-screen-orientation';
import { useIsFocused } from '@react-navigation/native';
import NumberFormat from 'react-number-format';



const avisar = (message) => {
	ToastAndroid.showWithGravityAndOffset(
		message,
		ToastAndroid.LONG,
		ToastAndroid.BOTTOM,
		25,
		50
	);

	return null;
};

meses = new Array("Janeiro", "Fevereiro", "Mar√ßo", "Abril", "Maio", "Junho", "Julho", "Agosto", "Setembro", "Outubro", "Novembro", "Dezembro");


export default function Salarios({ navigation }) {
	const [date, setDate] = useState(new Date());
	const [show, setShow] = useState(false);
	const [mode, setMode] = useState('date');

	const [contador, setContador] = useState(0);

	const onChange = (event, selectedDate) => {
		const currentDate = selectedDate || date;
		setDate(currentDate);
		setShow(Platform.OS === 'ios');
		dadosIniciais();
	};

	const showMode = (currentMode) => {
		setShow(true);
		setMode(currentMode);
	};

	const showDatepicker = () => {
		showMode('date');
	};

	const showTimepicker = () => {
		showMode('time');
	};


	const [salarios, setSalarios] = useState([]);

	async function dadosIniciais() {
		try {

			//avisar(" " + token )

			const headers = {
				'Authorization': 'Bearer ' + await AsyncStorage.getItem('@eContractRh:token')
			}


			const id_func = await AsyncStorage.getItem('@eContractRh:id_func');
			console.log("id: " + id_func)

			await api.get("/v1/protected/funcionarios/salarios/" + id_func, {
				headers: headers
			}).then(function (response) {
				setSalarios(response.data)
				console.log(" dados: " + response.data);

			});



		} catch (_err) {
			// avisar('Houve um problema com o login, verifique suas credenciais! ' + cpf + " " + senha );
			avisar(" " + _err);
			console.log(" " + _err)

		}

	}

	async function changeScreenOrientation() {
		await ScreenOrientation.lockAsync(ScreenOrientation.OrientationLock.LANDSCAPE);
	}



	useEffect(() => {


		const unsubscribe = navigation.addListener('focus', () => {
			console.log('Screen is focused');

			changeScreenOrientation();
			dadosIniciais();

		});

		return unsubscribe;


	}, []);



	return (

		<View style={{ width: '100%', height: '100%' }}>
			<View style={{ fontSize: 18, backgroundColor: 'rgba(255,255,255,1)' }}>
				{show && (
					<DateTimePicker
						style={{ widht: 200, fontSize: 18 }}
						value={date}
						mode={mode}
						display="default"
						onChange={onChange}
					/>
				)}
			</View>

			<View style={{ width: '100%' }}>
				<Text style={{ width: '100%', backgroundColor: '#006400', padding: 10, fontWeight: 'bold' , fontSize: 25, color: 'white', textAlign: 'left' }}> S√°larios </Text>
				<Button
				     color="#006400"
					onPress={showDatepicker}
					title={moment(date).format("MM/yyyy")}

				/>
				<ListaVertical dados={salarios} />
			</View>


		</View>

	);
};


const PontoDiario = ({ item }) => (
	<View style={styles.containerCol, { marginBottom: 10, backgroundColor: 'white' }}>

		<View style={{ flexDirection: 'row' }}>
			<View style={styles.coluna, { marginLeft: 40, marginBottom: 20, width: '100%' }}>

				<LinhaDados
					mes={item.mes}
					ano={item.ano}
					salario_base={item.salario_base}
					total_descontos={item.total_descontos}
					total_acrescimos={item.total_acrescimos}
					total_hora_extras={item.total_hora_extras}
				/>

			</View>


		</View>

	</View>
);

function currencyFormat(num) {
	return 'R$ ' + num.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
}

const LinhaDados = (props) => {

	return (
		<View style={styles.linha }>
			<View style={styles.col, {margin: 20}}>

				<View style={styles.linha}>
					<View >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'right' }} >M√™s: </Text>
					</View>
					<View >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'left' }} >{meses[props.mes]}</Text>
					</View>
				</View>
				<View style={styles.linha}>
					<View >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'right' }} >Ano: </Text>
					</View>
					<View >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'left' }} >{props.ano}</Text>
					</View>
				</View>
			</View>
			
			<View style={styles.col, {margin: 20}}>

				<View style={styles.linha}>
					<View  >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'right' }} >                S√°lario Base: </Text>
					</View>
					<View >
						<Text style={{ color: 'black',fontWeight: 'bold', fontSize: 16, textAlign: 'left' }} >{currencyFormat(props.salario_base)}</Text>

					</View>
				</View>

				<View style={styles.linha}>
					<View >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'right' }} >  Total de Acrescimos:</Text>
					</View>
					<View >
						<Text style={{ color: 'blue',  fontWeight: 'bold',fontSize: 16, textAlign: 'left' }} > {currencyFormat(props.total_acrescimos)}</Text>

					</View>
				</View>

				<View style={styles.linha}>
					<View >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'right' }} >Total de Horas Extras:</Text>
					</View>
					<View >
						<Text style={{ color: 'green',  fontWeight: 'bold', fontSize: 16, textAlign: 'left' }} > {currencyFormat(props.total_hora_extras)}</Text>

					</View>
				</View>

				<View style={styles.linha}>
					<View >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'right' }} >     Total de Descontos: </Text>
					</View>
					<View >
						<Text style={{ color: 'red',  fontWeight: 'bold', fontSize: 16, textAlign: 'left' }} >{currencyFormat(props.total_descontos)}</Text>

					</View>
				</View>

				<View style={styles.linha}>
					<View >
						<Text style={{ color: 'black', fontSize: 16, textAlign: 'right' }} >                 S√°lario Final: </Text>
					</View>
					<View >
						<Text style={{ color: 'red',  fontWeight: 'bold', fontSize: 16, textAlign: 'left' }} >
							{currencyFormat(props.salario_base + props.total_acrescimos + props.total_hora_extras - props.total_descontos)}
							</Text>

					</View>
				</View>

			</View>

		</View>
	);
}

const CardVertical = ({ item }) => {
	return (

		<PontoDiario
			item={item}
		/>


	);
}

const ListaVertical = (props) => {
	return (


		<FlatList
			numColumns={1}
			data={props.dados}
			renderItem={CardVertical}
			keyExtractor={(item) => item.id_salario.toString()}
			contentContainerStyle={{ paddingBottom: 100 }}
		/>

	);
}
