import React, { useState, useEffect } from 'react';
import { Text, Image, View, ImageBackground, TextInput, ToastAndroid, DatePickerIOSComponent } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import PropTypes from 'prop-types';
import api from '../../services/api';
import { StatusBar } from 'react-native';
import 'react-native-gesture-handler';
import { StyleSheet, ScrollView } from 'react-native';
import Orientation from 'react-native-orientation-locker';
import moment from 'moment';
import DateTimePicker from '@react-native-community/datetimepicker';
import { Button } from 'react-native-elements';
import * as ScreenOrientation from 'expo-screen-orientation';

const avisar = (message) => {
    ToastAndroid.showWithGravityAndOffset(
        message,
        ToastAndroid.LONG,
        ToastAndroid.BOTTOM,
        25,
        50
    );

    return null;
};



export default function Ponto({ navigation }) {

    const [dados, setDados] = useState(0);
    const [date, setDate] = useState(new Date());
    const [show, setShow] = useState(false);
    const [mode, setMode] = useState('date');


    const [contador, setContador] = useState(0);

    const onChange = (event, selectedDate) => {
        const currentDate = selectedDate || date;
        setDate(currentDate);
        setShow(Platform.OS === 'ios');
        dadosIniciais();
    };

    const showMode = (currentMode) => {
        setShow(true);
        setMode(currentMode);
    };

    const showDatepicker = () => {
        showMode('date');
    };

    const showTimepicker = () => {
        showMode('time');
    };

    async function dadosIniciais() {
        try {

            //avisar(" " + token )

            const headers = {
                'Authorization': 'Bearer ' + await AsyncStorage.getItem('@eContractRh:token')
            }


            const id_func = await AsyncStorage.getItem('@eContractRh:id_func');
            console.log("id: " + id_func)

            await api.get("/v1/protected/registroPonto/getRp/" + id_func + "/dia?data=" + moment(date).format("DD/MM/yyyy"), {
                headers: headers
            }).then(function (response) {
                setDados(response.data)
                console.log(" dados: " + response.data);

            });



        } catch (_err) {
            // avisar('Houve um problema com o login, verifique suas credenciais! ' + cpf + " " + senha );
            avisar(" " + _err);
            console.log(" " + _err)
        }

    }

    async function changeScreenOrientation() {
        await ScreenOrientation.lockAsync(ScreenOrientation.OrientationLock.ALL);
    }


    useEffect(() => {


        const unsubscribe = navigation.addListener('focus', () => {
            console.log('Screen is focused');

            changeScreenOrientation();
            dadosIniciais();

        });

        return unsubscribe;


    }, []);


    return (

        <View style={styles.container}>
            <StatusBar hidden />
            <ScrollView style={{ width: '100%', height: '100%' }}>
                <View style={styles.linha}>

                    <View style={{ fontSize: 18 }}>
                        {show && (
                            <DateTimePicker
                                style={{ widht: 200, fontSize: 18 }}
                                value={date}
                                mode={mode}
                                display="default"
                                onChange={onChange}
                            />
                        )}
                    </View>

                </View>
                <View style={styles.coluna, { backgroundColor: '#00008B' }}>
                    <View style={styles.linha}>
                        <View style={{ width: '100%' }}>
                            <Text style={{ color: 'white', fontWeight: 'bold', fontSize: 30, margin: 10, padding: 30, textAlign: 'center' }} > Registro do Dia </Text>
                        </View>
                    </View>
                    <View style={{ width: '100%', flexDirection: "row", justifyContent: 'center' }}>
                        <View style={{ width: 160 }}>
                            <Text style={{ color: 'white', fontWeight: 'bold', margin: 8, textAlign: 'right' }} >{dados.dia_da_semana} - </Text>
                        </View>
                        <View syte={{ alignItems: 'left' }}>
                            <Button
                                buttonStyle={styles.button}
                                onPress={showDatepicker}
                                title={moment(date).format("DD/MM/yyyy")}

                            />
                        </View>
                    </View>
                    <View style={styles.coluna, { backgroundColor: 'white' }}>
                        <View style={styles.linha}>
                            <View style={{ width: 150 }}>
                                <Text style={{ color: 'black', margin: 6, textAlign: 'right' }} >Rotina do Dia:</Text>
                            </View>
                            <View style={{ width: '100%' , margin: 3}}>
                                <Text style={{fontSize: 16}}>
                                    <Text style={{ color: 'black',  textAlign: 'left' }} >Das </Text>
                                    <Text style={{ color: 'black', fontWeight: 'bold',textAlign: 'left' }} >{dados.rotinaE1}</Text>
                                    <Text style={{ color: 'black',  textAlign: 'left' }} > às </Text>
                                    <Text style={{ color: 'black', fontWeight: 'bold', textAlign: 'left' }} >{dados.rotinaS1}</Text>

                                </Text>
                            </View>
                        </View>
                        <View style={styles.linha}>
                            <View style={{ width: 150 }}>
                                <Text style={{ color: 'black', margin: 3, textAlign: 'right' }} ></Text>
                            </View>
                            <View style={{ width: '100%' , margin: 3}}>
                                <Text style={{fontSize: 16}}>
                                    <Text style={{ color: 'black',  textAlign: 'left' }} >Das </Text>
                                    <Text style={{ color: 'black', fontWeight: 'bold',textAlign: 'left' }} >{dados.rotinaE2}</Text>
                                    <Text style={{ color: 'black',  textAlign: 'left' }} > às </Text>
                                    <Text style={{ color: 'black', fontWeight: 'bold', textAlign: 'left' }} >{dados.rotinaS2}</Text>

                                </Text>
                            </View>
                        </View>
                        <View style={{ width: '100%', backgroundColor: '#FF8C00' }}>
                            <Text style={{ color: 'white', margin: 3, fontSize: 20, padding: 15, textAlign: 'left', fontWeight: 'bold' }} >    Pontos:</Text>
                        </View>
                        <View style={styles.coluna, { backgroundColor: 'white' }}>
                            <View style={styles.linha}>
                                <View style={{ width: 200, fontSize: 20 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Hora Entrada 1: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'black', fontWeight: 'bold', margin: 3, fontSize: 20, textAlign: 'left' }} >{dados.entrada1} h</Text>
                                </View>
                            </View>
                            <View style={styles.linha}>
                                <View style={{ width: 200, fontSize: 20 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Hora Saída 1: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'black', fontWeight: 'bold', fontSize: 20, margin: 3, textAlign: 'left' }} >{dados.saida1} h</Text>
                                </View>
                            </View>

                            <View style={styles.linha}>
                                <View style={{ width: 200, fontSize: 20 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Hora Entrada 2: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'black', fontWeight: 'bold', fontSize: 20, margin: 3, textAlign: 'left' }} >{dados.entrada2} h</Text>
                                </View>
                            </View>

                            <View style={styles.linha}>
                                <View style={{ width: 200, fontSize: 20 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Hora Saída 2: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'black', fontWeight: 'bold', fontSize: 20, margin: 3, textAlign: 'left' }} >{dados.saida2} h</Text>
                                </View>
                            </View>


                            <View style={styles.linha}>
                                <View style={{ width: 200, fontSize: 20 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Hora Entrada 3: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'black', fontWeight: 'bold', fontSize: 20, margin: 3, textAlign: 'left' }} >{dados.entrada3} h</Text>
                                </View>
                            </View>

                            <View style={styles.linha}>
                                <View style={{ width: 200 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Hora Saída 3: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'black', fontWeight: 'bold', fontSize: 20, margin: 3, textAlign: 'left' }} >{dados.saida3} h</Text>
                                </View>
                            </View>

                            <View style={{ width: '100%' }, { backgroundColor: 'green' }}>
                                <Text style={{ color: 'white', fontWeight: 'bold', margin: 3, fontSize: 20, padding: 15, textAlign: 'left' }} >    Somas:</Text>
                            </View>

                            <View style={styles.linha}>
                                <View style={{ width: 200, fontSize: 20 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Horas Diárias: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'black', margin: 3, fontWeight: 'bold', fontSize: 20, textAlign: 'left' }} >{dados.hora_diaria} h</Text>
                                </View>
                            </View>

                            <View style={styles.linha}>
                                <View style={{ width: 200 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Horas Trabalhadas: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'blue', fontWeight: 'bold', margin: 3, fontSize: 20, textAlign: 'left' }} >{dados.hora_trabalhada} h</Text>
                                </View>
                            </View>

                            <View style={styles.linha}>
                                <View style={{ width: 200, heigh: '100%' }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Horas Extras: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'green', fontWeight: 'bold', margin: 3, fontWeight: 'bold', fontSize: 20, textAlign: 'left' }} >{dados.hora_extra} h</Text>
                                </View>
                            </View>

                            <View style={styles.linha}>
                                <View style={{ width: 200 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Horas Noturnas: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'brown', margin: 3, fontWeight: 'bold', fontSize: 20, textAlign: 'left' }} >{dados.hora_noturna} h</Text>
                                </View>
                            </View>
                            
                            <View style={styles.linha}>
                                <View style={{ width: 200 }}>
                                    <Text style={{ color: 'black', marginTop: 8, textAlign: 'right' }} >Horas Atrazo: </Text>
                                </View>
                                <View style={{ width: '100%' }}>
                                    <Text style={{ color: 'red', margin: 3, fontWeight: 'bold', fontSize: 20, textAlign: 'left' }} >{dados.hora_atrazo} h</Text>
                                </View>
                            </View>

                           

                        </View>
                    </View>

                </View>
            </ScrollView >
        </View>
    );


}



const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
    },
    button: {
        backgroundColor: 'blue',
        textAlign: 'left',

    },
    backgroundImage: {
        resizeMode: 'cover',
        position: 'absolute',
        width: '100%',
        height: '100%',
    },
    linha: {
        flexDirection: "row",
        borderColor: '#FFF',
        backgroundColor: 'rgba(0,0,0,0.0)', // 40% opaque
        color: 'black',
        fontSize: 18,
        fontWeight: 'bold'

    },

    coluna: {
        width: '100%',
        height: '100%',
        flexDirection: "column",
        borderColor: '#FFF',
        backgroundColor: 'rgba(152,251,152,0.2)', // 40% opaque
        color: 'black',
        fontSize: 18,
        fontWeight: 'bold'

    },
});
